document.addEventListener("keydown",function(e){if(e.key==="Escape")document.documentElement.classList.remove("modal-open"),document.querySelectorAll(".modal").forEach(function(e){e.classList.remove("is-active")});else if(e.key==="ArrowLeft"){const e=document.querySelectorAll(".modal"),t=Array.from(e).findIndex(e=>e.classList.contains("is-active"));t>0&&(e[t].classList.remove("is-active"),e[t-1].classList.add("is-active"))}else if(e.key==="ArrowRight"){const e=document.querySelectorAll(".modal"),t=Array.from(e).findIndex(e=>e.classList.contains("is-active"));t<e.length-1&&(e[t].classList.remove("is-active"),e[t+1].classList.add("is-active"))}})